mapper.select:
SELECT a.* FROM (SELECT rownum rno, r.rep_date, r.rep_num, r.mem_id, r.rep_id, r.rep_type, r.board_num, r.comm_num, m.mem_block
FROM report_table r, member_table m
WHERE r.rep_id= m.mem_id order by r.rep_date desc)a where a.rno between #{startPage} and #{endPage}

mapper.insert:
INSERT INTO
	report_table (rep_num, mem_id, rep_id, rep_type,rep_date
	, board_num
	, comm_num
	)
VALUES
	('r_'||sequ_report_num.nextval, #{memId}, #{repId}, #{repType},sysdate
	, #{boardNum}
	, #{commNum}
	)
