mapper.select:
SELECT b.* FROM (SELECT rownum rno,a.* 
FROM (
SELECT board_table.board_title, comm_contents, comm_date,board_table.board_num,board_table.board_type, comment_table.mem_id
FROM comment_table, board_table
WHERE board_table.board_num = comment_table.board_num and comment_table.mem_id = #{memId} 
ORDER BY comm_date desc)a)b where rno between #{startPage} and #{endPage}

mapper.update:
update 
comment_table 
set 
    comm_contents = #{commContents} 
where 
    comm_num =#{commNum} 
and 
    mem_id=#{memId}
